(function(){"use strict";angular.module("ProsCons").controller("ListCtrl",["$scope",function(e){e.items=e.$parent.list.items||[],e.newItem={title:"",type:1},e.add=function(){e.items.push({title:e.newItem.title,type:e.newItem.type}),e.newItem.title=""},e.remove=function(t){var n;n=e.items.indexOf(t),n>=0&&e.items.splice(n,1)},e.strType=function(e){return 1===e?"pros":"cons"}}])})();